import React, { Component }  from 'react';
import './slide.css';
import { Slide } from 'react-slideshow-image';
import blo from '../img/2162634.jpg';
import bla from '../img/diamond.png';
import bli from '../img/diamond-icon.png';

class Slides extends Component {
  constructor() {
    super();
    this.state = {
      slideImages: [
        blo,
        bla,
        bli
      ],
      message:[
        'holi',
        'chao',
        'pescao'
      ]
    };
  }

  render() {
    const properties = {
      duration: 5000,
      transitionDuration: 500,
      infinite: true,
      indicators: true
    };

    const { slideImages, message} = this.state;
    return (
      <div>
        <div className="slide-container">
          <Slide {...properties}>
            {slideImages.map((each, index) => (
              <div key={index} className="each-slide">
                <div style={{ backgroundImage: `url(${each})` }}>

                </div>

              </div>
            ))} 

            { message.map((eachMess, index) => (
                  <p key={index} className="each-message">
                    {message[index]}
                  </p>
                ))}      

          </Slide>
        </div>
      </div>
    );
  }
}

export default Slides;
